define(["controllers/controllers","config"],function(e,t){e.controller("clubController",["$scope",function(e){e.positions=t.app.positions,e.player={},e.players=[];var n=e.$bus.publish({channel:"players",topic:"player.list",data:{}});console.log("players ->",n),e.savePlayer=function(t){t.goals=_.isUndefined(t.goals)?0:t.goals;if(t.id){var n=e.players;_.find(n,function(e){return e.id==t.id}),e.players=n}else e.players.push({id:_.uniqueId(),name:t.name,position:t.position,goals:t.goals});e.player={}},e.editPlayer=function(t){e.player=t},e.deletePlayer=function(t){e.players=_.filter(e.players,function(e){return e.id!=t})},e.clearForm=function(){e.player={}},e.passIt=function(){e.$bus.publish({channel:"orders",topic:"order.new",data:{name:"Cengiz",profession:"Engineer"}})}}])});